import React, { Component } from "react";
import {
  Tabs,
  Tab,
  Grid,
  Cell,
  Card,
  CardTitle,
  CardActions,
  CardMenu,
  Button,
  IconButton,
  CardText,
} from "react-mdl";

// import Container from "../Container"

class Profolio extends Component {
  constructor(props) {
    super(props);
    this.state = { activeTab: 0 };
  }

  // add if statement in a method to be rendered
  toggleCategories() {
    // use to render active tab using index id
    if (this.state.activeTab === 0) {
      return (
        <Card shadow={5} style={{ minWidth: "450", margin: "auto" }}>
          <CardTitle
            style={{
              color: "#FFF",
              height: "176",
              background: "url() center / cover",
            }}
          ></CardTitle>
        </Card>
      );
    } else if (this.state.activeTab === 1) {
      return (
        <Card shadow={5} style={{ minWidth: "450", margin: "auto" }}>
          <CardTitle
            style={{
              color: "#FFF",
              height: "176",
              background:
                "url(https://carolenesw.github.io/food-finder-P1/) center / cover",
            }}
          >
            Food finder
          </CardTitle>
          <CardText>
            JQuery app that display nearby restaurants with delivery options,
            price, location and cuisines.
          </CardText>
          <CardActions border>
            <button colored>Food finder</button>
            <button colored>Repository</button>
          </CardActions>
        </Card>
      );
    } else if (this.state.activeTab === 2) {
      return (
        <div>
          <h1>This is Express</h1>
        </div>
      );
    } else if (this.state.activeTab === 3) {
      return (
        <div>
          <h1>This is MongoDB</h1>
        </div>
      );
    } else if (this.state.activeTab === 4) {
      return (
        <div>
          <h1>This is Nodejs</h1>
        </div>
      );
    } else if (this.state.activeTab === 5) {
      return (
        <div>
          <h1>This is JavaScript</h1>
        </div>
      );
    }
  }

  render() {
    return (
      <div className="project-type">
        <h2>Profolio</h2>
        <Tabs
          activeTab={this.state.activeTab}
          onChange={(tabId) => this.setState({ activeTab: tabId })}
          ripple
        >
          <Tab>React</Tab>
          <Tab>JQuery</Tab>
          <Tab>Express</Tab>
          <Tab>MongoDB</Tab>
          <Tab>Nodejs</Tab>
          <Tab>JavaScript</Tab>
        </Tabs>

        <section className="project-grid">
          <Grid className="project-grid">
            <Cell col={12}>
              <div className="content">{this.toggleCategories()}</div>
            </Cell>
          </Grid>
        </section>
      </div>
    );
  }
}

export default Profolio;
